contracts/PoolPriceCustomFeed.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title PoolPriceCustomFeed
 * @notice FDC-verified custom price feed for a single Uniswap V3-style pool.
 * @dev Deploy one instance per pool/feed; implements IICustomFeed for FTSO compatibility.
 */

/**
 * @title IICustomFeed
 * @notice Interface for custom FTSO feeds
 */
interface IICustomFeed {
    function feedId() external view returns (bytes21 _feedId);
    function read() external view returns (uint256 value);
    function decimals() external pure returns (int8);
    function calculateFee() external pure returns (uint256 _fee);
    function getCurrentFeed()
        external
        payable
        returns (uint256 _value, int8 _decimals, uint64 _timestamp);
}

/**
 * @title IFdcVerification
 * @notice Interface for Flare Data Connector verification
 */
interface IFdcVerification {
    function verifyEVMTransaction(
        IEVMTransaction.Proof calldata _proof
    ) external view returns (bool);
}

/**
 * @title IEVMTransaction
 * @notice EVMTransaction attestation type structures
 */
interface IEVMTransaction {
    struct Proof {
        bytes32[] merkleProof;
        Response data;
    }

    struct Response {
        bytes32 attestationType;
        bytes32 sourceId;
        uint64 votingRound;
        uint64 lowestUsedTimestamp;
        RequestBody requestBody;
        ResponseBody responseBody;
    }

    struct RequestBody {
        bytes32 transactionHash;
        uint16 requiredConfirmations;
        bool provideInput;
        bool listEvents;
        uint32[] logIndices;
    }

    struct ResponseBody {
        uint64 blockNumber;
        uint64 timestamp;
        address sourceAddress;
        bool isDeployment;
        address receivingAddress;
        uint256 value;
        bytes input;
        uint8 status;
        Event[] events;
    }

    struct Event {
        uint32 logIndex;
        address emitterAddress;
        bytes32[] topics;
        bytes data;
        bool removed;
    }
}

/**
 * @title IFlareContractRegistry
 * @notice Interface for Flare's contract registry
 */
interface IFlareContractRegistry {
    function getContractAddressByName(
        string calldata _name
    ) external view returns (address);
}

contract PoolPriceCustomFeed is IICustomFeed {
    // ==================== Immutable Configuration ====================

    /// @notice Unique feed identifier (starts with 0x21 for custom feeds)
    bytes21 private immutable _feedId;

    /// @notice Decimal precision (6 decimals to match tokens)
    int8 private constant DECIMALS = 6;

    /// @notice keccak256("PriceRecorded(address,uint160,int24,uint128,address,address,uint256,uint256)")
    bytes32 private constant PRICE_RECORDED_TOPIC =
        keccak256(
            "PriceRecorded(address,uint160,int24,uint128,address,address,uint256,uint256)"
        );

    /// @notice PriceRecorder contract whose events we trust for this feed.
    address public immutable priceRecorderAddress;

    /// @notice Uniswap V3 pool address this feed is locked to.
    address public immutable poolAddress;

    /// @notice FDC verification contract used for proof validation.
    IFdcVerification private immutable fdcVerification;

    /// @notice Token0 decimals (for decimal-aware price calculation)
    uint8 public immutable token0Decimals;

    /// @notice Token1 decimals (for decimal-aware price calculation)
    uint8 public immutable token1Decimals;

    /// @notice Whether to invert the price (for market convention)
    bool public immutable invertPrice;

    // ==================== Mutable State ====================

    /// @notice Contract owner
    address public owner;

    /// @notice Latest verified price
    uint256 public latestValue;

    /// @notice Timestamp of last update
    uint64 public lastUpdateTimestamp;

    /// @notice Total number of updates received
    uint256 public updateCount;

    /// @notice Whether feed is accepting updates
    bool public acceptingUpdates;

    /// @notice Total gas used for all verifications
    uint256 public totalGasUsedForVerification;

    /// @notice Total proofs successfully verified
    uint256 public totalProofsVerified;

    // ==================== Events ====================

    event FeedUpdated(
        uint256 indexed value,
        uint64 timestamp,
        uint256 blockNumber,
        address indexed updater
    );

    event ProofVerified(
        bytes32 indexed transactionHash,
        uint256 value,
        uint64 timestamp
    );

    event UpdatesPaused();
    event UpdatesResumed();
    event OwnershipTransferred(
        address indexed previousOwner,
        address indexed newOwner
    );

    // ==================== Modifiers ====================

    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }

    // ==================== Constructor ====================

    /**
     * @notice Initialize the custom feed (one instance per pool).
     * @param _priceRecorder Address of the trusted PriceRecorder contract.
     * @param _poolAddress Address of the single pool whose prices we will accept.
     * @param _feedName UTF-8 name for feed ID generation (e.g., "FXRP/USDTo-PRICE").
     * @param _fdcVerificationAddress Address of the FDC verification contract.
     * @param _token0Decimals Decimals of token0 in the pool (for price adjustment).
     * @param _token1Decimals Decimals of token1 in the pool (for price adjustment).
     * @param _invertPrice Whether to invert the price (for market convention).
     */
    constructor(
        address _priceRecorder,
        address _poolAddress,
        string memory _feedName,
        address _fdcVerificationAddress,
        uint8 _token0Decimals,
        uint8 _token1Decimals,
        bool _invertPrice
    ) {
        require(_priceRecorder != address(0), "Invalid recorder address");
        require(_poolAddress != address(0), "Invalid pool address");
        require(_fdcVerificationAddress != address(0), "Invalid FDC address");
        require(
            _token0Decimals > 0 && _token0Decimals <= 18,
            "Invalid token0 decimals"
        );
        require(
            _token1Decimals > 0 && _token1Decimals <= 18,
            "Invalid token1 decimals"
        );

        owner = msg.sender;
        priceRecorderAddress = _priceRecorder;
        poolAddress = _poolAddress;
        acceptingUpdates = true;
        token0Decimals = _token0Decimals;
        token1Decimals = _token1Decimals;
        invertPrice = _invertPrice;

        // Generate feed ID: 0x21 + hex(feedName) + zero padding
        _feedId = _generateFeedId(_feedName);

        // Set FDC verification contract
        fdcVerification = IFdcVerification(_fdcVerificationAddress);
    }

    /**
     * @notice Generates feed ID from name
     * @param name Feed name in UTF-8 (max 20 bytes)
     * @return bytes21 Feed ID (0x21 + hex-encoded name + padding)
     */
    function _generateFeedId(
        string memory name
    ) private pure returns (bytes21) {
        bytes memory nameBytes = bytes(name);
        require(
            nameBytes.length > 0 && nameBytes.length <= 20,
            "Invalid name length"
        );

        bytes21 id;
        // Set category byte (0x21 for custom feeds)
        id = bytes21(uint168(0x21) << 160);

        // Add hex-encoded name
        for (uint256 i = 0; i < nameBytes.length; i++) {
            // Shift amount: 152, 144, 136, ... (decreasing by 8)
            uint8 shiftAmount = uint8(152 - i * 8);
            id |= bytes21(uint168(uint8(nameBytes[i])) << shiftAmount);
        }

        return id;
    }

    // ==================== Core Update Function ====================

    /**
     * @notice Updates feed with FDC-verified price data
     * @param _proof EVMTransaction proof from FDC
     * @dev Anyone can call this to update the feed with valid proof
     */
    function updateFromProof(IEVMTransaction.Proof calldata _proof) external {
        require(acceptingUpdates, "Updates paused");

        uint256 gasStart = gasleft();

        // Step 1: Verify FDC proof authenticity
        require(
            fdcVerification.verifyEVMTransaction(_proof),
            "Invalid FDC proof"
        );

        // Step 2: Extract response from proof
        IEVMTransaction.Response memory response = _proof.data;

        // Step 3: Validate transaction source (must be from PriceRecorder)
        require(
            response.responseBody.receivingAddress == priceRecorderAddress,
            "Wrong contract address"
        );

        // Step 3a: Validate transaction succeeded (defensive coding)
        require(response.responseBody.status == 1, "Transaction failed");

        // Step 4: Parse events for PriceRecorded event
        (uint256 newPrice, uint64 timestamp) = _parseEvents(
            response.responseBody.events
        );

        // Step 5: Store verified price
        latestValue = newPrice;
        lastUpdateTimestamp = timestamp;
        updateCount++;

        // Track statistics
        uint256 gasUsed = gasStart - gasleft();
        totalGasUsedForVerification += gasUsed;
        totalProofsVerified++;

        emit FeedUpdated(newPrice, timestamp, block.number, msg.sender);
        emit ProofVerified(
            response.requestBody.transactionHash,
            newPrice,
            timestamp
        );
    }

    /**
     * @notice Parses PriceRecorded event from transaction logs
     * @param events Array of events from FDC proof
     * @return price Calculated price with decimals
     * @return timestamp Event timestamp
     */
    function _parseEvents(
        IEVMTransaction.Event[] memory events
    ) private view returns (uint256 price, uint64 timestamp) {
        bool found = false;

        for (uint256 i = 0; i < events.length; i++) {
            IEVMTransaction.Event memory evt = events[i];

            // Check emitter address (must be PriceRecorder)
            if (evt.emitterAddress != priceRecorderAddress) continue;

            // Check event signature (topics[0])
            if (
                evt.topics.length > 0 && evt.topics[0] == PRICE_RECORDED_TOPIC
            ) {
                // Decode indexed parameter: topics[1] = pool address
                address eventPool = address(uint160(uint256(evt.topics[1])));
                require(eventPool == poolAddress, "Wrong pool");

                // Decode non-indexed parameters from data field
                (
                    uint160 sqrtPriceX96,
                    , // tick - unused
                    , // liquidity - unused
                    , // token0 - unused
                    , // token1 - unused
                    uint256 eventTimestamp,

                ) = abi.decode(
                        // blockNumber - unused
                        evt.data,
                        (
                            uint160,
                            int24,
                            uint128,
                            address,
                            address,
                            uint256,
                            uint256
                        )
                    );

                // Calculate human-readable price
                price = _calculatePrice(sqrtPriceX96);
                timestamp = uint64(eventTimestamp);
                found = true;
                break;
            }
        }

        require(found, "PriceRecorded event not found");
    }

    /**
     * @notice Converts sqrtPriceX96 to human-readable price
     * @param sqrtPriceX96 Square root price in Q64.96 format
     * @return uint256 Price with DECIMALS precision
     * @dev Applies decimal adjustment for pools with different token decimals
     */
    function _calculatePrice(
        uint160 sqrtPriceX96
    ) private view returns (uint256) {
        // Uniswap V3 price formula: price = (sqrtPriceX96 / 2^96)^2
        // This gives token1/token0 ratio in native units
        // For tokens with different decimals, we need to adjust:
        // actual_price = raw_price * 10^(decimals0 - decimals1)

        uint256 Q96 = 2 ** 96;

        // Calculate raw price with HIGH precision (18 decimals) to avoid losing precision
        uint256 numerator = uint256(sqrtPriceX96) *
            uint256(sqrtPriceX96) *
            (10 ** 18);
        uint256 denominator = Q96 * Q96;
        uint256 price = numerator / denominator;

        // Apply decimal adjustment if tokens have different decimals
        int256 decimalAdjustment = int256(uint256(token0Decimals)) -
            int256(uint256(token1Decimals));
        if (decimalAdjustment > 0) {
            // token0 has more decimals than token1
            // Multiply by 10^(decimals0 - decimals1)
            price = price * (10 ** uint256(decimalAdjustment));
        } else if (decimalAdjustment < 0) {
            // token1 has more decimals than token0
            // Divide by 10^(decimals1 - decimals0)
            price = price / (10 ** uint256(-decimalAdjustment));
        }

        // Scale down from 18 decimals to 6 decimals for storage
        price = price / (10 ** 12);

        // Apply price inversion if configured (for market convention)
        if (invertPrice && price > 0) {
            // Invert: 1 / price (maintaining 6 decimal precision)
            price = (10 ** 12) / price;
        }

        // Sanity checks
        require(price > 0, "Price must be positive");
        require(price < type(uint128).max, "Price exceeds maximum");

        return price;
    }

    // ==================== IICustomFeed Implementation ====================

    /**
     * @notice Returns the feed identifier
     * @return bytes21 Feed ID starting with 0x21
     */
    function feedId() external view override returns (bytes21) {
        return _feedId;
    }

    /**
     * @notice Returns the latest verified price
     * @return uint256 Price value with DECIMALS precision
     */
    function read() external view override returns (uint256) {
        require(latestValue > 0, "No data available");
        return latestValue;
    }

    /**
     * @notice Returns decimal precision
     * @return int8 Number of decimals (6)
     */
    function decimals() external pure override returns (int8) {
        return DECIMALS;
    }

    /**
     * @notice Calculates fee for reading (free for this implementation)
     * @return uint256 Fee amount (0)
     */
    function calculateFee() external pure override returns (uint256) {
        return 0; // Free to read
    }

    /**
     * @notice Returns current feed data with timestamp
     * @return _value uint256 Current price
     * @return _decimals int8 Decimal precision
     * @return _timestamp uint64 Last update time
     */
    function getCurrentFeed()
        external
        payable
        override
        returns (uint256 _value, int8 _decimals, uint64 _timestamp)
    {
        require(latestValue > 0, "No data available");
        return (latestValue, DECIMALS, lastUpdateTimestamp);
    }

    // ==================== Admin Functions ====================

    /**
     * @notice Pause feed updates (emergency)
     */
    function pauseUpdates() external onlyOwner {
        acceptingUpdates = false;
        emit UpdatesPaused();
    }

    /**
     * @notice Resume feed updates
     */
    function resumeUpdates() external onlyOwner {
        acceptingUpdates = true;
        emit UpdatesResumed();
    }

    /**
     * @notice Transfer ownership
     * @param newOwner New owner address
     */
    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "Invalid address");
        address oldOwner = owner;
        owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }

    // ==================== View Functions ====================

    /**
     * @notice Get comprehensive feed information
     * @return info Struct with all feed metadata
     */
    function getFeedInfo() external view returns (FeedInfo memory info) {
        return
            FeedInfo({
                feedId: _feedId,
                latestValue: latestValue,
                decimals: DECIMALS,
                lastUpdate: lastUpdateTimestamp,
                updateCount: updateCount,
                priceRecorder: priceRecorderAddress,
                poolAddress: poolAddress,
                acceptingUpdates: acceptingUpdates,
                avgGasPerUpdate: totalProofsVerified > 0
                    ? totalGasUsedForVerification / totalProofsVerified
                    : 0,
                totalProofsVerified: totalProofsVerified
            });
    }

    struct FeedInfo {
        bytes21 feedId;
        uint256 latestValue;
        int8 decimals;
        uint64 lastUpdate;
        uint256 updateCount;
        address priceRecorder;
        address poolAddress;
        bool acceptingUpdates;
        uint256 avgGasPerUpdate;
        uint256 totalProofsVerified;
    }
}

contracts/PriceRecorder.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title PriceRecorder
 * @notice Helper contract for recording Uniswap V3 pool prices on-chain
 * @dev Creates verifiable on-chain records for FDC attestation
 */

// Minimal Uniswap V3 Pool interface
interface IUniswapV3Pool {
    function slot0()
        external
        view
        returns (
            uint160 sqrtPriceX96,
            int24 tick,
            uint16 observationIndex,
            uint16 observationCardinality,
            uint16 observationCardinalityNext,
            uint8 feeProtocol,
            bool unlocked
        );

    function liquidity() external view returns (uint128);
    function token0() external view returns (address);
    function token1() external view returns (address);
    function fee() external view returns (uint24);
}

contract PriceRecorder {
    // ==================== State Variables ====================

    /// @notice Contract owner (deployer)
    address public owner;

    /// @notice Global recording state (can pause/resume)
    bool public isRecording;

    /// @notice Minimum seconds between updates
    uint256 public updateInterval;

    /// @notice Enabled pools mapping
    mapping(address => bool) public enabledPools;

    /// @notice Pool metadata and state
    mapping(address => PoolInfo) public poolInfo;

    /// @notice List of all pools (for enumeration)
    address[] public poolList;

    /// @notice Pool information struct
    struct PoolInfo {
        address token0;
        address token1;
        uint24 fee;
        uint256 lastUpdate;
        uint256 updateCount;
    }

    // ==================== Events ====================

    /// @notice Emitted when price is recorded
    event PriceRecorded(
        address indexed pool,
        uint160 sqrtPriceX96,
        int24 tick,
        uint128 liquidity,
        address token0,
        address token1,
        uint256 timestamp,
        uint256 blockNumber
    );

    /// @notice Emitted when recording status changes
    event RecordingStatusChanged(bool isRecording);

    /// @notice Emitted when update interval changes
    event UpdateIntervalChanged(uint256 oldInterval, uint256 newInterval);

    /// @notice Emitted when pool is enabled
    event PoolEnabled(
        address indexed pool,
        address token0,
        address token1,
        uint24 fee
    );

    /// @notice Emitted when pool is disabled
    event PoolDisabled(address indexed pool);

    /// @notice Emitted for emergency price recordings
    event EmergencyPriceRecorded(address indexed pool, string reason);

    /// @notice Emitted when ownership is transferred
    event OwnershipTransferred(
        address indexed previousOwner,
        address indexed newOwner
    );

    // ==================== Modifiers ====================

    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }

    modifier whenRecording() {
        require(isRecording, "Recording is paused");
        _;
    }

    modifier respectsInterval(address pool) {
        require(
            block.timestamp >= poolInfo[pool].lastUpdate + updateInterval,
            "Update interval not elapsed"
        );
        _;
    }

    // ==================== Constructor ====================

    /**
     * @notice Initialize the PriceRecorder contract
     * @param _initialInterval Starting update interval in seconds (e.g., 300 = 5 minutes)
     */
    constructor(uint256 _initialInterval) {
        require(_initialInterval > 0, "Invalid interval");

        owner = msg.sender;
        isRecording = true;
        updateInterval = _initialInterval;

        emit RecordingStatusChanged(true);
        emit UpdateIntervalChanged(0, _initialInterval);
    }

    // ==================== Core Functions ====================

    /**
     * @notice Records current price from a Uniswap V3 pool
     * @param pool Address of the Uniswap V3 pool
     * @dev Callable by anyone if interval has elapsed
     */
    function recordPrice(
        address pool
    ) external whenRecording respectsInterval(pool) {
        require(enabledPools[pool], "Pool not enabled");
        _recordPrice(pool);
    }

    /**
     * @notice Records prices for multiple pools in one transaction
     * @param pools Array of pool addresses
     * @dev More gas efficient for updating multiple pools
     * @dev Maximum 50 pools per batch to prevent gas limit issues
     */
    function recordPriceBatch(address[] calldata pools) external whenRecording {
        require(pools.length <= 50, "Batch too large");

        for (uint256 i = 0; i < pools.length; i++) {
            address pool = pools[i];
            if (
                enabledPools[pool] &&
                block.timestamp >= poolInfo[pool].lastUpdate + updateInterval
            ) {
                _recordPrice(pool);
            }
        }
    }

    /**
     * @notice Emergency price recording bypassing all checks
     * @param pool Pool address
     * @param reason Explanation for emergency recording
     * @dev Owner only, ignores intervals and pause state
     */
    function emergencyRecordPrice(
        address pool,
        string calldata reason
    ) external onlyOwner {
        require(enabledPools[pool], "Pool not enabled");
        _recordPrice(pool);
        emit EmergencyPriceRecorded(pool, reason);
    }

    /**
     * @notice Internal function to record pool price
     * @param pool Pool address
     */
    function _recordPrice(address pool) private {
        // Query pool state
        (
            uint160 sqrtPriceX96,
            int24 tick,
            , // observationIndex - unused
            , // observationCardinality - unused
            , // observationCardinalityNext - unused
            , // feeProtocol - unused
            bool unlocked
        ) = IUniswapV3Pool(pool).slot0();

        require(unlocked, "Pool is locked");

        // Get additional data
        uint128 liquidity = IUniswapV3Pool(pool).liquidity();

        // Use cached token addresses from poolInfo (gas optimization)
        PoolInfo storage info = poolInfo[pool];
        address token0 = info.token0;
        address token1 = info.token1;

        // Update state
        info.lastUpdate = block.timestamp;
        info.updateCount++;

        // Emit comprehensive event (this is what FDC will attest to!)
        emit PriceRecorded(
            pool,
            sqrtPriceX96,
            tick,
            liquidity,
            token0,
            token1,
            block.timestamp,
            block.number
        );
    }

    // ==================== Admin Functions ====================

    /**
     * @notice Enable or disable price recording globally
     * @param _isRecording True to enable, false to pause
     */
    function setRecording(bool _isRecording) external onlyOwner {
        require(isRecording != _isRecording, "Already in this state");
        isRecording = _isRecording;
        emit RecordingStatusChanged(_isRecording);
    }

    /**
     * @notice Update minimum interval between recordings
     * @param _newInterval New interval in seconds
     */
    function setUpdateInterval(uint256 _newInterval) external onlyOwner {
        require(_newInterval > 0, "Interval must be positive");
        require(_newInterval <= 1 days, "Interval too long");

        uint256 oldInterval = updateInterval;
        updateInterval = _newInterval;

        emit UpdateIntervalChanged(oldInterval, _newInterval);
    }

    /**
     * @notice Enable a new pool for price recording
     * @param pool Pool address to enable
     */
    function enablePool(address pool) external onlyOwner {
        require(pool != address(0), "Invalid address");
        require(!enabledPools[pool], "Already enabled");

        // Validate it's a real Uniswap V3 pool by calling slot0()
        try IUniswapV3Pool(pool).slot0() returns (
            uint160,
            int24,
            uint16,
            uint16,
            uint16,
            uint8,
            bool
        ) {
            // Get pool metadata
            address token0 = IUniswapV3Pool(pool).token0();
            address token1 = IUniswapV3Pool(pool).token1();
            uint24 fee = IUniswapV3Pool(pool).fee();

            // Validate pool properties
            require(token0 != token1, "Tokens must be different");
            require(
                fee == 100 || fee == 500 || fee == 3000 || fee == 10000,
                "Invalid fee tier"
            );

            // Check if pool is already in poolList (prevent duplicates on re-enable)
            bool inList = false;
            for (uint256 i = 0; i < poolList.length; i++) {
                if (poolList[i] == pool) {
                    inList = true;
                    break;
                }
            }

            // Store pool info
            enabledPools[pool] = true;
            poolInfo[pool] = PoolInfo({
                token0: token0,
                token1: token1,
                fee: fee,
                lastUpdate: 0,
                updateCount: 0
            });

            // Only add to poolList if not already present
            if (!inList) {
                poolList.push(pool);
            }

            emit PoolEnabled(pool, token0, token1, fee);
        } catch {
            revert("Invalid Uniswap V3 pool");
        }
    }

    /**
     * @notice Disable a pool from recording
     * @param pool Pool address to disable
     */
    function disablePool(address pool) external onlyOwner {
        require(enabledPools[pool], "Pool not enabled");

        enabledPools[pool] = false;

        emit PoolDisabled(pool);
    }

    /**
     * @notice Transfer ownership to a new address
     * @param newOwner New owner address
     */
    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "Invalid address");
        address oldOwner = owner;
        owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }

    // ==================== View Functions ====================

    /**
     * @notice Check if a pool can be updated now
     * @param pool Pool address to check
     * @return bool True if update is allowed
     */
    function canUpdate(address pool) external view returns (bool) {
        if (!isRecording || !enabledPools[pool]) return false;
        return block.timestamp >= poolInfo[pool].lastUpdate + updateInterval;
    }

    /**
     * @notice Get time until next update is allowed for a pool
     * @param pool Pool address
     * @return uint256 Seconds until next update (0 if ready now)
     */
    function timeUntilNextUpdate(address pool) external view returns (uint256) {
        if (!enabledPools[pool]) return type(uint256).max;

        uint256 nextAllowedTime = poolInfo[pool].lastUpdate + updateInterval;
        if (block.timestamp >= nextAllowedTime) {
            return 0;
        }
        return nextAllowedTime - block.timestamp;
    }

    /**
     * @notice Get list of all enabled pools
     * @return address[] Array of pool addresses
     */
    function getEnabledPools() external view returns (address[] memory) {
        uint256 count = 0;
        for (uint256 i = 0; i < poolList.length; i++) {
            if (enabledPools[poolList[i]]) count++;
        }

        address[] memory enabled = new address[](count);
        uint256 index = 0;
        for (uint256 i = 0; i < poolList.length; i++) {
            if (enabledPools[poolList[i]]) {
                enabled[index] = poolList[i];
                index++;
            }
        }

        return enabled;
    }

    /**
     * @notice Get detailed info about a pool
     * @param pool Pool address
     * @return PoolInfo struct with pool details
     * @dev Returns info even for disabled pools (useful for diagnostics)
     */
    function getPoolInfo(address pool) external view returns (PoolInfo memory) {
        require(poolInfo[pool].token0 != address(0), "Pool never added");
        return poolInfo[pool];
    }

    /**
     * @notice Get total number of pools (enabled + disabled)
     * @return uint256 Total pool count
     */
    function getPoolCount() external view returns (uint256) {
        return poolList.length;
    }
}

contracts/PriceRelay.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title PriceRelay
 * @notice Receives relayed prices from external chains and emits attestable events
 * @dev Security-hardened with token binding, monotonicity guards, and deviation checks
 * 
 * This contract enables price feeds from chains not directly supported by FDC's EVMTransaction
 * attestation (e.g., Arbitrum, Base, Optimism, Polygon). Authorized relayers fetch prices
 * off-chain and relay them here, then FDC attests the relay transaction on Flare.
 * 
 * Security Features:
 * - Token binding: Pool tokens are locked on enablePool(), verified on each relay
 * - Future timestamp rejection: sourceTimestamp must be <= block.timestamp
 * - Monotonicity: sourceBlockNumber must strictly increase
 * - Rate limiting: minRelayInterval between relays per pool
 * - Deviation check: Rejects price swings > MAX_DEVIATION_BPS (50%)
 * - Access control: Only authorized relayers can call relayPrice()
 * - Emergency pause: Owner can pause all relay operations
 */
contract PriceRelay {
    // ==================== State Variables ====================

    address public owner;
    bool public isActive;

    /// @notice Authorized relayer addresses
    mapping(address => bool) public authorizedRelayers;

    /// @notice Supported source chains
    mapping(uint256 => bool) public supportedChains;

    /// @notice Enabled pools per chain: chainId => poolAddress => enabled
    mapping(uint256 => mapping(address => bool)) public enabledPools;

    /// @notice Last relay timestamp per pool
    mapping(uint256 => mapping(address => uint256)) public lastRelayTime;

    /// @notice Minimum time between relays (prevents spam)
    uint256 public minRelayInterval;

    /// @notice Maximum age of source data accepted
    uint256 public maxPriceAge;

    /// @notice Maximum price deviation allowed (basis points) - 50%
    uint256 public constant MAX_DEVIATION_BPS = 5000;

    // ==================== Pool Configuration ====================

    /// @notice Pool configuration with token binding and tracking
    /// @dev Token addresses are bound on enablePool() and verified on each relay
    struct PoolConfig {
        address token0;           // Bound on enablePool - immutable per pool
        address token1;           // Bound on enablePool - immutable per pool
        uint256 lastBlockNumber;  // For monotonicity check
        uint256 lastSqrtPriceX96; // For deviation check
    }

    /// @notice Pool configurations: chainId => poolAddress => config
    mapping(uint256 => mapping(address => PoolConfig)) public poolConfig;

    // ==================== Events ====================

    /// @notice Emitted when price is relayed - THIS IS WHAT FDC WILL ATTEST
    /// @dev Contains all data needed for PoolPriceCustomFeed to parse and verify
    event PriceRelayed(
        uint256 indexed sourceChainId,
        address indexed poolAddress,
        uint160 sqrtPriceX96,
        int24 tick,
        uint128 liquidity,
        address token0,
        address token1,
        uint256 sourceTimestamp,
        uint256 sourceBlockNumber,
        uint256 relayTimestamp,
        address relayer
    );

    event RelayerAuthorized(address indexed relayer);
    event RelayerRevoked(address indexed relayer);
    event ChainEnabled(uint256 indexed chainId);
    event ChainDisabled(uint256 indexed chainId);
    event PoolEnabled(uint256 indexed chainId, address indexed pool, address token0, address token1);
    event PoolDisabled(uint256 indexed chainId, address indexed pool);
    event RelayPaused();
    event RelayUnpaused();
    event ConfigUpdated(uint256 minRelayInterval, uint256 maxPriceAge);

    // ==================== Modifiers ====================

    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }

    modifier onlyAuthorizedRelayer() {
        require(authorizedRelayers[msg.sender], "Not authorized relayer");
        _;
    }

    modifier whenActive() {
        require(isActive, "Relay paused");
        _;
    }

    // ==================== Constructor ====================

    /**
     * @notice Deploy PriceRelay with initial configuration
     * @param _minRelayInterval Minimum seconds between relays for same pool
     * @param _maxPriceAge Maximum age of source data in seconds
     * @dev Deployer becomes owner and first authorized relayer
     */
    constructor(uint256 _minRelayInterval, uint256 _maxPriceAge) {
        require(_minRelayInterval > 0, "Invalid interval");
        require(_maxPriceAge > 0, "Invalid max age");

        owner = msg.sender;
        isActive = true;
        minRelayInterval = _minRelayInterval;
        maxPriceAge = _maxPriceAge;

        // Owner is initially an authorized relayer
        authorizedRelayers[msg.sender] = true;
        emit RelayerAuthorized(msg.sender);
    }

    // ==================== Core Relay Function ====================

    /**
     * @notice Relay price data from an external chain
     * @dev Only callable by authorized relayers. Includes all security guards.
     * @param sourceChainId The chain ID where the pool exists
     * @param poolAddress The Uniswap V3 pool address on source chain
     * @param sqrtPriceX96 Current sqrtPriceX96 from slot0
     * @param tick Current tick from slot0
     * @param liquidity Current liquidity from pool
     * @param token0 Token0 address (must match registered tokens)
     * @param token1 Token1 address (must match registered tokens)
     * @param sourceTimestamp Block timestamp from source chain
     * @param sourceBlockNumber Block number from source chain
     */
    function relayPrice(
        uint256 sourceChainId,
        address poolAddress,
        uint160 sqrtPriceX96,
        int24 tick,
        uint128 liquidity,
        address token0,
        address token1,
        uint256 sourceTimestamp,
        uint256 sourceBlockNumber
    ) external onlyAuthorizedRelayer whenActive {
        // Basic validation
        require(supportedChains[sourceChainId], "Chain not supported");
        require(enabledPools[sourceChainId][poolAddress], "Pool not enabled");
        require(sqrtPriceX96 > 0, "Invalid price");

        PoolConfig storage config = poolConfig[sourceChainId][poolAddress];

        // SECURITY: Token binding check - tokens must match what was registered
        require(token0 == config.token0 && token1 == config.token1, "Token mismatch");

        // SECURITY: Future timestamp rejection - can't relay prices from the future
        require(sourceTimestamp <= block.timestamp, "Future timestamp");

        // SECURITY: Freshness check - price data can't be too old
        require(block.timestamp - sourceTimestamp <= maxPriceAge, "Price data too old");

        // SECURITY: Monotonicity - block numbers must strictly increase
        require(sourceBlockNumber > config.lastBlockNumber, "Stale block number");

        // SECURITY: Rate limiting - enforce minimum interval between relays
        require(
            block.timestamp >= lastRelayTime[sourceChainId][poolAddress] + minRelayInterval,
            "Relay interval not elapsed"
        );

        // SECURITY: Deviation check (skip on first relay when lastSqrtPriceX96 is 0)
        if (config.lastSqrtPriceX96 > 0) {
            uint256 deviation = _calculateDeviation(config.lastSqrtPriceX96, sqrtPriceX96);
            require(deviation <= MAX_DEVIATION_BPS, "Price deviation too high");
        }

        // Update state
        config.lastBlockNumber = sourceBlockNumber;
        config.lastSqrtPriceX96 = sqrtPriceX96;
        lastRelayTime[sourceChainId][poolAddress] = block.timestamp;

        // Emit event - THIS IS WHAT FDC WILL ATTEST
        emit PriceRelayed(
            sourceChainId,
            poolAddress,
            sqrtPriceX96,
            tick,
            liquidity,
            token0,
            token1,
            sourceTimestamp,
            sourceBlockNumber,
            block.timestamp,
            msg.sender
        );
    }

    // ==================== Internal Functions ====================

    /**
     * @notice Calculate price deviation in basis points
     * @param oldPrice Previous sqrtPriceX96 value
     * @param newPrice New sqrtPriceX96 value
     * @return Deviation in basis points (0-10000)
     */
    function _calculateDeviation(uint256 oldPrice, uint256 newPrice) internal pure returns (uint256) {
        if (oldPrice == 0) return 0;
        uint256 diff = oldPrice > newPrice ? oldPrice - newPrice : newPrice - oldPrice;
        return (diff * 10000) / oldPrice;
    }

    // ==================== Relayer Management ====================

    /**
     * @notice Authorize an address to relay prices
     * @param relayer Address to authorize
     */
    function authorizeRelayer(address relayer) external onlyOwner {
        require(relayer != address(0), "Invalid address");
        require(!authorizedRelayers[relayer], "Already authorized");
        authorizedRelayers[relayer] = true;
        emit RelayerAuthorized(relayer);
    }

    /**
     * @notice Revoke relayer authorization
     * @param relayer Address to revoke
     */
    function revokeRelayer(address relayer) external onlyOwner {
        require(authorizedRelayers[relayer], "Not authorized");
        authorizedRelayers[relayer] = false;
        emit RelayerRevoked(relayer);
    }

    // ==================== Chain Management ====================

    /**
     * @notice Enable a source chain for relaying
     * @param chainId Chain ID to enable
     */
    function enableChain(uint256 chainId) external onlyOwner {
        require(chainId > 0, "Invalid chain ID");
        require(!supportedChains[chainId], "Already enabled");
        supportedChains[chainId] = true;
        emit ChainEnabled(chainId);
    }

    /**
     * @notice Disable a source chain
     * @param chainId Chain ID to disable
     */
    function disableChain(uint256 chainId) external onlyOwner {
        require(supportedChains[chainId], "Not enabled");
        supportedChains[chainId] = false;
        emit ChainDisabled(chainId);
    }

    // ==================== Pool Management ====================

    /**
     * @notice Enable a pool for price relaying with token binding
     * @dev SECURITY: Token addresses are bound here and verified on every relay
     * @param chainId Source chain ID
     * @param pool Pool address on source chain
     * @param token0 Token0 address (will be bound)
     * @param token1 Token1 address (will be bound)
     */
    function enablePool(
        uint256 chainId, 
        address pool, 
        address token0, 
        address token1
    ) external onlyOwner {
        require(supportedChains[chainId], "Chain not supported");
        require(pool != address(0), "Invalid pool");
        require(token0 != address(0) && token1 != address(0), "Invalid tokens");
        require(token0 != token1, "Tokens must differ");
        require(!enabledPools[chainId][pool], "Already enabled");

        enabledPools[chainId][pool] = true;
        poolConfig[chainId][pool] = PoolConfig({
            token0: token0,
            token1: token1,
            lastBlockNumber: 0,
            lastSqrtPriceX96: 0
        });

        emit PoolEnabled(chainId, pool, token0, token1);
    }

    /**
     * @notice Disable a pool
     * @param chainId Source chain ID
     * @param pool Pool address
     */
    function disablePool(uint256 chainId, address pool) external onlyOwner {
        require(enabledPools[chainId][pool], "Not enabled");
        enabledPools[chainId][pool] = false;
        emit PoolDisabled(chainId, pool);
    }

    // ==================== Configuration ====================

    /**
     * @notice Update relay interval
     * @param interval New minimum interval in seconds
     */
    function setMinRelayInterval(uint256 interval) external onlyOwner {
        require(interval > 0, "Invalid interval");
        minRelayInterval = interval;
        emit ConfigUpdated(interval, maxPriceAge);
    }

    /**
     * @notice Update maximum price age
     * @param age New maximum age in seconds
     */
    function setMaxPriceAge(uint256 age) external onlyOwner {
        require(age > 0, "Invalid age");
        maxPriceAge = age;
        emit ConfigUpdated(minRelayInterval, age);
    }

    // ==================== Emergency Controls ====================

    /**
     * @notice Pause all relay operations
     */
    function pause() external onlyOwner {
        require(isActive, "Already paused");
        isActive = false;
        emit RelayPaused();
    }

    /**
     * @notice Resume relay operations
     */
    function unpause() external onlyOwner {
        require(!isActive, "Not paused");
        isActive = true;
        emit RelayUnpaused();
    }

    /**
     * @notice Transfer ownership
     * @param newOwner New owner address
     */
    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "Invalid address");
        require(newOwner != owner, "Same owner");
        owner = newOwner;
    }

    // ==================== View Functions ====================

    /**
     * @notice Check if a pool can be relayed to
     * @param chainId Source chain ID
     * @param pool Pool address
     * @return True if relay is possible
     */
    function canRelay(uint256 chainId, address pool) external view returns (bool) {
        if (!isActive) return false;
        if (!supportedChains[chainId]) return false;
        if (!enabledPools[chainId][pool]) return false;
        if (block.timestamp < lastRelayTime[chainId][pool] + minRelayInterval) return false;
        return true;
    }

    /**
     * @notice Get pool configuration
     * @param chainId Source chain ID
     * @param pool Pool address
     * @return Pool configuration struct
     */
    function getPoolConfig(uint256 chainId, address pool) external view returns (PoolConfig memory) {
        return poolConfig[chainId][pool];
    }

    /**
     * @notice Get time until next relay is allowed
     * @param chainId Source chain ID
     * @param pool Pool address
     * @return Seconds until next relay (0 if can relay now)
     */
    function timeUntilNextRelay(uint256 chainId, address pool) external view returns (uint256) {
        uint256 nextAllowed = lastRelayTime[chainId][pool] + minRelayInterval;
        if (block.timestamp >= nextAllowed) return 0;
        return nextAllowed - block.timestamp;
    }
}

